mapper.update:
update show_info
id=#{id}, content=#{content}, publicType=#{publicType}, city=#{city}, commentNum=#{commentNum}, praiseNum=#{praiseNum}, shareNum=#{shareNum}, creater=#{creater}, createTime=#{createTime}, type=#{type}, imgUuid=#{imgUuid}, videoUuid=#{videoUuid}, status=#{status}, videoPreviewUuid=#{videoPreviewUuid},
where id=#{id}

mapper.select:
   select 

   from show_info


				and id=#{id} 


				and content=#{content} 


				and publicType=#{publicType} 


				and city=#{city} 


				and commentNum=#{commentNum} 


				and praiseNum=#{praiseNum} 


				and shareNum=#{shareNum} 


				and creater=#{creater} 


				and createTime=#{createTime} 


				and type=#{type} 


				and imgUuid=#{imgUuid} 


				and videoUuid=#{videoUuid} 


				and status=#{status} 


				and videoPreviewUuid=#{videoPreviewUuid}

select si.id, si.content, si.publicType, si.city, si.commentNum, si.praiseNum, si.shareNum, si.creater, si.createTime, si.type, si.imgUuid, si.videoUuid, si.videoPreviewUuid, si.status, spi.urlPath as videoUrl, spiv.urlPath as videoPreviewUrl, group_concat(spi2.urlPath) as imgUrls from show_info si left join system_picture_info spi on si.videoUuid = spi.uuid left join system_picture_info spiv on si.videoPreviewUuid = spiv.uuid left join show_info_pictures sip on sip.showId = si.id left join system_picture_info spi2 on spi2.uuid=sip.imgUuid
and si.creater=#{creater}
and si.status=#{status}
group by si.id
order by si.${sort} ${order}

